DROP TABLE IF EXISTS alerts;
CREATE TABLE alerts (
	id INTEGER PRIMARY KEY AUTOINCREMENT,
	vehicle_id INTEGER,
	driver_id INTEGER,
	alert_type TEXT NOT NULL,
	severity TEXT DEFAULT 'Medium',
	title TEXT NOT NULL,
	description TEXT,
	location TEXT,
	latitude REAL,
	longitude REAL,
	speed REAL,
	threshold_value TEXT,
	actual_value TEXT,
	status TEXT DEFAULT 'Unhandled',
	handler TEXT,
	handled_at TEXT,
	handle_result TEXT,
	alert_time TEXT NOT NULL,
	created_at TEXT NOT NULL DEFAULT (datetime('now')),
	updated_at TEXT NOT NULL DEFAULT (datetime('now')),
	deleted_at TEXT
);

DROP TABLE IF EXISTS drivers;
CREATE TABLE drivers (
	id INTEGER PRIMARY KEY AUTOINCREMENT,
	name TEXT NOT NULL,
	gender TEXT DEFAULT 'Male',
	phone TEXT NOT NULL,
	id_card TEXT,
	license_no TEXT,
	license_type TEXT DEFAULT 'Class 3',
	license_expire_date TEXT,
	hire_date TEXT,
	department TEXT,
	status TEXT NOT NULL DEFAULT 'Active',
	assigned_vehicle_id INTEGER,
	emergency_contact TEXT,
	emergency_phone TEXT,
	address TEXT,
	driving_years INTEGER DEFAULT 0,
	violation_count INTEGER DEFAULT 0,
	accident_count INTEGER DEFAULT 0,
	rating REAL DEFAULT 5.0,
	avatar_url TEXT,
	notes TEXT,
	created_at TEXT NOT NULL DEFAULT (datetime('now')),
	updated_at TEXT NOT NULL DEFAULT (datetime('now')),
	deleted_at TEXT
);

DROP TABLE IF EXISTS fuel_records;
CREATE TABLE fuel_records (
	id INTEGER PRIMARY KEY AUTOINCREMENT,
	vehicle_id INTEGER NOT NULL,
	driver_id INTEGER,
	fuel_date TEXT NOT NULL,
	fuel_type TEXT NOT NULL,
	quantity REAL NOT NULL,
	unit_price REAL NOT NULL,
	total_cost REAL NOT NULL,
	mileage_at_fuel INTEGER,
	gas_station TEXT,
	payment_method TEXT DEFAULT 'Company Card',
	invoice_no TEXT,
	is_full_tank INTEGER DEFAULT 1,
	notes TEXT,
	created_at TEXT NOT NULL DEFAULT (datetime('now')),
	updated_at TEXT NOT NULL DEFAULT (datetime('now')),
	deleted_at TEXT
);

DROP TABLE IF EXISTS gps_tracking;
CREATE TABLE gps_tracking (
	id INTEGER PRIMARY KEY AUTOINCREMENT,
	vehicle_id INTEGER NOT NULL,
	latitude REAL NOT NULL,
	longitude REAL NOT NULL,
	speed REAL DEFAULT 0,
	heading REAL,
	altitude REAL,
	location_name TEXT,
	status TEXT DEFAULT 'Moving',
	recorded_at TEXT NOT NULL,
	created_at TEXT NOT NULL DEFAULT (datetime('now'))
);

